function clickToEditField(e,t){e=e.toString(),t=t.toString();var n="";if(document.getElementById(e).style.display="none","undefined"!=typeof document.getElementById(e).innerHTML.toString())var n=document.getElementById(e).innerHTML.toString();else if("undefined"!=typeof document.getElementById(e).value.toString())var n=document.getElementById(e).value.toString();$("#"+e).after('<div id="'+e+'-editbox"><input type="text" id="'+e+'-edit"  value="'+n.toString()+'" /><br><input type="button" value="Save" onclick="clickToEditSave(\''+e+"','"+t+'\')" /><input type="button" value="Cancel" onclick="clickToEditCancel(\''+e+"')\" /></div>")}function clickToEditSave(e,t){var n=document.getElementById(e+"-edit").value;document.getElementById(e).innerHTML=n,clickToEditCancel(e),$.post(t,{field:e,value:n},function(e){alert(e)})}function clickToEditCancel(e){$("#"+e+"-editbox").remove(),document.getElementById(e).style.display=""}
